preview-css:
/*
  Edit this CSS file to change styles of the preview.
  If you need to revert it back, use the "Reset" button.
*/

/**
* Text Cell
*/
body {
  background-color: #010101;
  color: #dddddd;
  /*padding: 20px;*/
  /*margin: -20px;*/
}

a{ color:#00ffff; /*无链接时颜色*/ }
a {font-size: 30px}
a:hover{ color: yellow;/*鼠标经过链接时颜色*/}
a:active{ color: yellow; /*鼠标点击链接时颜色*/}
a:visited { color: #00cccc}


.text-cell {
  font-size: 15px;
}

/**
* Code Cell
*/

.code-cell {
  font-size: 15px;
  background-color: #dddddd;
  margin-top: 25px;
  margin-bottom: 25px;
  margin-left: 0px;
  margin-right: 0px;
  padding: 5px;
  border-width: 0px;
  border-color: white;
  border-style: solid;
  border-radius: 2px
}

/**
* Markdown Cell
*/

.markdown-cell {
  font-size: 15px;
  color: #fdfdfd;
  background-color: #333333;
  margin-top: 25px;
  margin-bottom: 25px;
  margin-left: 0px;
  margin-right: 0px;
  padding-top: -20px;
  padding-bottom: -20px;
  padding-left: 10px;
  padding-right: 10px;
  border-width: 2px;
  border-color: white;
  border-style: solid;
  border-radius: 10px
}

/**
* LaTeX Cell
*/

.latex-cell {
  font-size: 15px;
}

